import React from 'react';
import axios from 'axios';

const options = {
    method: 'GET',
    url: 'https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD,JPY,EUR',

  };
  
class Cryptocurrency extends React.Component {

    constructor(props)
    {
        super(props) 
        this.state ={
            USD: 0,
            EUR: 0,
            JPY: 0
        }


        axios.request(options).then(function (response) {
            console.log(response.data)
            this.setState(
             {
                USD: response.data.USD,
                EUR: response.data.EUR,
                JPY: response.data.JPY
             }
            )
            
         }).catch(function (error) {
             console.error(error);
         })

         console.log(this.data.USD)
    }
    crypto=[
        {
            name: 'Биткоин',
            ticker: 'BTC',
            year: '2009',
            link: 'https://ru.wikipedia.org/wiki/Биткойн'

        },

        {
            name: 'Эфириум',
            ticker: 'ETH',
            year: '2015',
            link: 'https://ru.wikipedia.org/wiki/Ethereum'
        },

        {
            name: 'Tether',
            ticker: 'USDT',
            year: '2015',
            link: 'https://ru.wikipedia.org/wiki/Tether'
        },

        {
            name: 'BNB',
            ticker: 'BNB',
            year: '2017',
            link: 'https://www.binance.com/ru/bnb'
        }
    ]

    render()
    {
        return (<div className="info">
            <h2>Список доступных криптовалют</h2>
            {this.crypto.map((el)=>( <a href={el.link} id="link" >
            <div className="crypto">
                <h3>Название: {el.name}</h3>
                <h3>Тикер: {el.ticker}</h3> 
                <p>Год выхода: {el.year}</p>
               </div>
               </a>
            ))}
        </div>

        )
    }
}
export default Cryptocurrency
