import axios from 'axios';
import React from 'react';

const options = {
    method: 'GET',
    url: 'https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD,JPY,EUR',

  };
  
class Main extends React.Component {
    constructor(props)
    {
        super(props)    
        this.clickButton=this.clickButton.bind(this)
    
    }

    state={
        USD: 0,
        EUR: 0,
        JPY: 0,

    }
    render()
    {
        return (
            <div className="main">
                
                <img id='image' src="https://master-saydinga.ru/wp-content/uploads/kriptovalyuta.jpg"></img><br></br>
                <select id="list">
                    <option>Биткоин</option>
                    <option>Эфириум</option>
                    <option>Tether</option>
                    <option>BNB</option>
                </select><br></br>
                <input id="data" type="date" /><br></br>
                <button id="myButton" onClick={clickButton} >Готово</button>
                <span>Цена в евро:{this.state.EUR}</span><br></br>
                <span>Цена в долларах:{this.state.USD}</span><br></br>
                <span>Цена в иенах:{this.state.JPY}</span><br></br>
            </div>
        )
    }

    clickButton()
    {
        axios.request(options).then(function (response) {
            this.setState(
             {
                 USD: response.data.USD,
                 EUR: response.data.EUR,
                 JPY: response.data.JPY
             }
            )
            
             console.log(response.data.USD);
         }).catch(function (error) {
             console.error(error);
         });

    }
}
export default Main
