import axios from 'axios';
import React from 'react';

const options = {
    method: 'GET',
    url: 'https://min-api.cryptocompare.com/data/pricehistorical?fsym=BTC&tsyms=USD&ts=1652680400',

  };//Дневная пара OHLCV от TS

  /*const options = {
    method: 'GET',
    url: 'https://l4chsalter-alternative-me-crypto-v1.p.rapidapi.com/v2/ticker/(id,name)',
    headers: {
      'X-RapidAPI-Key': '3e48e55c63msheacd6d577155f71p16348cjsn4d580b711657',
      'X-RapidAPI-Host': 'l4chsalter-alternative-me-crypto-v1.p.rapidapi.com'
    }
  };*/
  
class Main extends React.Component {
    constructor(props)
    {
        super(props) 
        this.state ={
            USD: 0,
            EUR: 0,
            JPY: 0
        }

        this.clickButton=this.clickButton.bind(this);

    }

    clickButton ()
    {
        axios.request(options).then((response) => {
            console.log(response.data)
            let normDate= 1680257986
            let date = new Intl.DateTimeFormat('ru-RU', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: 'true' }).format(normDate)
            console.log(date)
            //console.log(normDate)
            console.log(1680257986)

            let date2=new Date(document.getElementById('date').value)


            this.setState(
                {
                    USD: response.data.USD,
                    EUR: response.data.EUR,
                    JPY: response.data.JPY
                }
             )

         }).catch(function (error) {
             console.error(error);
         })

    }
    
    render()
    {
        return (
            <div className="main">
                
                <img id='image' src="https://master-saydinga.ru/wp-content/uploads/kriptovalyuta.jpg"></img><br></br>
                <select id="list">
                    <option>Биткоин</option>
                    <option>Эфириум</option>
                    <option>Tether</option>
                    <option>BNB</option>
                </select><br></br>
                <input id="date" type="date" /><br></br>
                <button id="myButton" onClick={this.clickButton}>Готово</button><br></br>
                <span className='price'>EUR: {this.state.EUR}</span><br></br>
                <span className='price' >USD: {this.state.USD}</span><br></br>
                <span className='price' >JPY: {this.state.JPY}</span><br></br>
            </div>
        )
    }

}
export default Main
